mapper.insert:
insert into SubClass
(
	scSerialNumber
	,mcserialNumber
	,foodCode
	,sImg
	,sName
	,unit
	,price
	,location
	,eDate
)
values
(
	sb_seq.nextval
	,#{mcserialNumber}
	,rpad(ABS(dbms_random.random),2)||'-'||rpad(ABS(dbms_random.random),3)
	,#{sImg}
	,#{sName}
	,#{unit}
	,#{price}
	,#{location}
	,#{eDate}
)
